# Directorio donde se guardarán los sprites
output_dir = "frontend/public/assets/sprites"
os.makedirs(output_dir, exist_ok=True)

# URL base
base_url = "https://img.pokemondb.net/sprites/home/normal/"

for name in pokemon_names:
    sprite_url = f"{base_url}{name}.png"
    response = requests.get(sprite_url)
    if response.status_code == 200:  # Si la imagen existe
        with open(os.path.join(output_dir, f"{name}.png"), "wb") as f:
            f.write(response.content)
        print(f"Descargado: {name}")
    else:
        print(f"Sprite no encontrado: {name}")